      if (password === "Bharadwaj183") {
        navigate("/admin/dashboard");
      } else {
        navigate("/admin/error");
      }
    } else {
      navigate("/admin/error");
    }
  };
  return (
    <div id="pirateHome">
      <div id="pirateDiv">
        Enter admin Email :{" "}
        <input
          onChange={(event) => setAdminEmail(event.target.value)}
          type="email"
        />
